'use strict';
// Game object to handle scorekeeping and to expose high level functionality to player
let randName = function () {
var objSlot = {};
var labelMulti =[];
let slotMachine;
let listName={};
let totalItem=0;
let totalDisplay = 0;
this.setName = function(name,total=3){
  labelMulti = name;
  totalDisplay = total;
  let totalName = labelMulti.length;
  let slotObj = $('.random-slot-machine .random-score-board .random-reels .random-slot');
  let nameObj={};
  totalItem = slotObj.length;
  let totalSplit = parseInt(totalName/total);
  let j=0;
  labelMulti = shuffleArray(labelMulti);
  slotObj.each(function(i) {
    if(i<total){
      obj = $(this).children('.random-item-container');
      obj.html('');
      nameObj[i]=[];
      listName[i]=[];
      for(let s=0;s<totalSplit;s++){
        nameObj[i].push(labelMulti[j]);
        //-------------
        listName[i].push({
                        item: labelMulti[j],
                        color: '#C86428'
                      })
        //-------------
        if(s == 0){
          obj.append(`<div class="item">?</div>`);
        }
        obj.append(`<div class="item">${labelMulti[j]}</div>`);
        //-------------
        j++;
      }
      //-----------
      objSlot[i]={
        combination: nameObj[i],
        color: '#C86428'
      };
    }
  });
}
this.init = function () {
  slotMachine = new SlotMachine(objSlot,listName,totalDisplay);
  slotMachine.init();
};
this.run = function (callback) {
  slotMachine.start(function (slotResult) {
    checkResult(slotResult, function (wc) {
      callback(wc);
    });
  });
};
let checkResult = function (result, callback) {
  callback(result);
  return;
};
};

// Slot machine object for exposing high level control over slot objects
let SlotMachine = function (combinations,listName,totalItem) {
let slots = [];
let slotResults = [];
this.winningCombinations = combinations;

this.init = () => {
  let $container={};
  let items ={};
  for(let i=0;i<totalItem;i++){ $container[i]=  $('#slot-'+(i+1)+'>.random-item-container');
    items[i] = shuffle(listName[i]);
    slots.push(new Slot($container[i], items[i]));
  }
};
this.start = callback => {
  let counter = 0;
  slots.forEach(slot => {
    slot.setSpeed();
    slot.start(currentItem => {
      slotResults.push(currentItem);
      counter++;
      if (counter === slots.length) {
        let result = getResults();
        callback(result);
      }
    });
  });
};
// Return results of slot run
function getResults() {
  let result = slotResults;
  slotResults = [];
  return result;
}
// Shuffle items for randomization
function shuffle(arr) {
  let i, j, x;
  for (i = arr.length; i; i--) {
    j = Math.floor(Math.random() * i);
    x = arr[i - 1];
    arr[i - 1] = arr[j];
    arr[j] = x;
  }
  return arr;
};
};

// Slot object for handling individual slots
let Slot = function (container, items) {
//------------
let speedOption=[100,215,300,80,150,115,147,200,165,270];
let speed = speedOption[Math.floor(Math.random() * 9)];
//------------
this.items = items;
this.$container = container;
let isRunning = false;
let interval = speed;
this.setSpeed = function(){
  interval = speedOption[Math.floor(Math.random() * 9)];
}
this.start = function (callback) {
  isRunning = true;
  this.$container.velocity({
    top: -75 },
  'linear', interval, () => {
    this.runSlot();
  });

  // Stop running after timeout
  let randomOffset = Math.random() < 0.5 ? -1 : 1;
  window.setTimeout(() =>{
    isRunning = false;
    this.stopSlot(currentItem => {
      callback(currentItem);
    });
  }, 3000 + 2 * interval + randomOffset * interval);
};
this.runSlot = function () {
  if (isRunning) {
    let $container = this.$container;
    let $firstEl = $container.find('div:first-child');
    $container.append($firstEl);
    $container.css({
      top: 100 });

    $container.velocity({
      top: -75 },
    'linear', interval, () => {
      $container.append($firstEl);
      this.runSlot();
    });
  }
};
this.stopSlot = function (callback) {
  // Bounce effect on stop
  let $container = this.$container;
  $container.velocity({
    top: 30 },
  'ease-out', 200);
  $container.velocity({
    top: -15 },
  'ease-out', 250);
  $container.velocity({
    top: 7 },
  'ease-out', 125);
  $container.velocity({
    top: -3 },
  'ease-out', 100);
  $container.velocity({
    top: 0 },
  'ease-out', 85, () => {
    let currentItem = $(container).children()[0];
    callback($(currentItem).text());
  });
};
};

