      for(const element of lastResultMulti) {
          labelMulti.push(element);
      }
      temp_txt = labelMulti.toString();
      if(temp_txt){
        temp_txt = temp_txt.replace(/,/g, "\n")
      }
      $('#txt_random_name_multi').val(temp_txt);
    }
